/**
 * Output functions available in pricing scripts
 */

/**
 * Output a debug message to the user
 * @param message - Debug message to display
 */
declare function debug(message: string): void;

/**
 * Output an alert message to the user
 * @param message - Alert message to display
 */
declare function alert(message: string): void;

/**
 * Output a warning message to the user
 * @param message - Warning message to display
 */
declare function warning(message: string): void;

/**
 * Output an error message to the user
 * @param message - Error message to display
 */
declare function error(message: string): void;

/**
 * Output a message to the user
 * @param message - Message to display
 */

/**
 * Output a message to the console/logs
 * @param message - Message to log to console
 */
declare function consoleLog(message: string): void;



 